SELECT current.id, current.period, current.scope_id, current.store_code, current.name, s.name AS store_name, current.score, current.rank_country, current.rank_scope, current.valid_ind, current.creator, current.create_time, current.updater, current.update_time, current.score - IFNULL(last.score, 0) AS score_diff, IFNULL(last.rank_country, 1) - current.rank_country AS rank_country_diff, IFNULL(last.rank_scope, 1) - current.rank_scope AS rank_scope_diff FROM store s LEFT JOIN score current ON current.store_code = s.code AND current.period = #{current_period}
AND current.scope_id = #{scope_id}
LEFT JOIN score last ON last.store_code = s.code AND last.period = #{last_period} WHERE 1 = 1
AND s.id IN
#{store_code}

SELECT current.id, current.period, current.scope_id, current.store_code, current.name, s.name AS store_name, current.score, current.rank_country, current.rank_scope, current.valid_ind, current.creator, current.create_time, current.updater, current.update_time, current.score - IFNULL(last.score, 0) AS score_diff, IFNULL(last.rank_country, 1) - current.rank_country AS rank_country_diff, IFNULL(last.rank_scope, 1) - current.rank_scope AS rank_scope_diff FROM score current LEFT JOIN score last ON last.store_code = current.store_code and last.scope_id = current.scope_id AND last.period = #{last_period} LEFT JOIN province s ON s.code = current.store_code WHERE current.scope_id = 3 AND current.period = #{current_period} AND current.store_code IN
#{store_code}
AND s.code IN
#{permission_code}

